using System;
using System.Threading.Tasks;
using System.IO;
using PCLStorage;
using Xamarin.Forms;
using ClientWebService;

namespace BikeBook
{
    public class ImageSerializer
    {
        public static string CachePath { get; set; }

        public const string SAMPLEIMAGE = "";

        public static void SetCachePath(string cachepath)
        {
            CachePath = cachepath;
        }

        /**
         *  Takes a file in the filesystem and returns a Base46-encoded string of the file's contents.
         *  
         *  @param string inputFilePath - the filesystem path for a file to serialize
         *  @return string - a string containing the file's contents in base64 form
         */
        internal string SerializeFromFile(string inputFilePath)
        {
            if ((inputFilePath != null) && (inputFilePath.Length > 0))
            {
                IFile ImageFile = FileSystem.Current.GetFileFromPathAsync(inputFilePath).Result;
                using (Stream ImageFileStream = ImageFile.OpenAsync(FileAccess.Read).Result)
                {
                    Byte[] inArray = new Byte[(int)ImageFileStream.Length];
                    ImageFileStream.Read(inArray, 0, (int)ImageFileStream.Length);
                    ImageFileStream.Dispose();
                    return Convert.ToBase64String(inArray);
                }
            }
            else
            {
                return string.Empty;
            }
        }

        /**
         *  Converts a base64 blob into the source bytes and writes the contents to a file.
         */
        internal void DeserializeToFile(string inputBase64, string outputFilePath)
        {
            IFolder WriteFolder = FileSystem.Current.GetFolderFromPathAsync(Path.GetDirectoryName(outputFilePath)).Result;
            IFile WriteFile = WriteFolder.CreateFileAsync(Path.GetFileName(outputFilePath), CreationCollisionOption.ReplaceExisting).Result;
            using (Stream ImageFileStream = WriteFile.OpenAsync(FileAccess.ReadAndWrite).Result)
            {
                Byte[] outArray = Convert.FromBase64String(inputBase64);
                ImageFileStream.Write(outArray, 0, outArray.Length);
                ImageFileStream.Dispose();
            }
        }

        /**
         *  Takes a base64 blob representing an image file and saves it to the device's cache.
         *  
         *  Names the image using a new GUID
         *  This function will parse the saved image and append the appropriate extension for jpg, bmp, png, gif and tif images.
         *  
         *  @param string inputBase64 - a base64-encoded string of data to convert and save
         *  @return string - the filepath where the image was saved to
         */
        internal ImageSource DeserializeImageToCache(string inputBase64)
        {
            if (inputBase64 != null && inputBase64.Length >= 100)
            {
                string TargetPath = Path.Combine(CachePath, Guid.NewGuid().ToString("N"));
                DeserializeToFile(inputBase64, TargetPath);
                //TargetPath = AddImageFileExtension(TargetPath);
                Task<IFile> ImageGetTask = FileSystem.Current.GetFileFromPathAsync(TargetPath);
                ImageGetTask.Wait();
                IFile ImageFile = ImageGetTask.Result;
                Stream ImageStream = ImageFile.OpenAsync(FileAccess.Read).Result;

                return ImageSource.FromStream(() => { return ImageStream; });
            }
            else
            {
                return null;
            }

        }

        internal ImageSource GetProfilePicture(string userEmail)
        {
            if ((userEmail != null) && (userEmail.Length > 0))
            {
                User loadedUser = Service.Instance.GetUser(Service.Instance.Email, userEmail);
                return DeserializeImageToCache(loadedUser.picture);
            }
            else
            {
                return null;
            }
        }


        private string AddImageFileExtension(string targetPath)
        {
            String NewFileName = string.Empty;
            IFile ImageFile = FileSystem.Current.GetFileFromPathAsync(targetPath).Result;
            Stream ImageFileStream = ImageFile.OpenAsync(FileAccess.Read).Result;
            byte[] inArray = new byte[8];
            ImageFileStream.Read(inArray, 0, 8);
            ImageFileType ImageType = GetImageTypeFromData(inArray);
            ImageFileStream.Dispose();
            switch (ImageType)
            {
                case ImageFileType.IMAGE_FILE_BMP: NewFileName = ImageFile.Name + ".bmp"; break;
                case ImageFileType.IMAGE_FILE_PNG: NewFileName = ImageFile.Name + ".png"; break;
                case ImageFileType.IMAGE_FILE_GIF: NewFileName = ImageFile.Name + ".gif"; break;
                case ImageFileType.IMAGE_FILE_TIFF: NewFileName = ImageFile.Name + ".tif"; break;
                case ImageFileType.IMAGE_FILE_JPG: NewFileName = ImageFile.Name + ".jpg"; break;
            }
            ImageFile.RenameAsync(NewFileName);
            return Path.Combine(Path.GetDirectoryName(ImageFile.Path), NewFileName);
        }

        private enum ImageFileType
        {
            IMAGE_FILE_JPG,      // joint photographic experts group - .jpeg or .jpg
            IMAGE_FILE_PNG,      // portable network graphics
            IMAGE_FILE_GIF,      // graphics interchange format 
            IMAGE_FILE_TIFF,     // tagged image file format
            IMAGE_FILE_BMP,      // Microsoft bitmap format
            IMAGE_FILE_INVALID,  // unidentified image types.
        };


        /**
         *  Checks the header of an image to see if it's a known image type
         */
        private ImageFileType GetImageTypeFromData(byte[] imageHeader)
        {
            // The image types we're interested in all have known headers:
            //
            // .jpg:  FF D8 FF
            // .png:  89 50 4E 47 0D 0A 1A 0A
            // .tiff: 49 49 2A 00
            //        4D 4D 00 2A
            //
            // .gif:  GIF87a      
            //        GIF89a
            // .bmp:  BM 


            switch (imageHeader[0])
            {
                case 0xFF:
                    if (ByteArrayStartsWith(imageHeader, 0xFF, 0xD8, 0xFF))
                    {
                        return ImageFileType.IMAGE_FILE_JPG;
                    }
                    else
                    {
                        return ImageFileType.IMAGE_FILE_INVALID;
                    }

                case 0x89:
                    if (ByteArrayStartsWith(imageHeader, 0x89, 0x50, 0x4E, 0x47, 0x0D, 0x0A, 0x1A, 0x0A))
                    {
                        return ImageFileType.IMAGE_FILE_PNG;
                    }
                    else
                    {
                        return ImageFileType.IMAGE_FILE_INVALID;
                    }
                case 0x49:
                    if (ByteArrayStartsWith(imageHeader, 0x49, 0x49, 0x2A, 0x00))
                    {
                        return ImageFileType.IMAGE_FILE_TIFF;
                    }
                    else
                    {
                        return ImageFileType.IMAGE_FILE_INVALID;
                    }
                case 0x4D:
                    if (ByteArrayStartsWith(imageHeader, 0x4D, 0x4D, 0x000, 0x2A))
                    {
                        return ImageFileType.IMAGE_FILE_TIFF;
                    }
                    else
                    {
                        return ImageFileType.IMAGE_FILE_INVALID;
                    }
            }

            string DataAsString = Convert.ToString(imageHeader);
            switch (DataAsString[0])
            {

                case 'G':
                    return (!DataAsString.StartsWith("GIF87a") ||
                             !DataAsString.StartsWith("GIF89a")) ?
                       ImageFileType.IMAGE_FILE_GIF : ImageFileType.IMAGE_FILE_INVALID;



                case 'B':
                    return ((DataAsString[1] == 'M')) ?
                        ImageFileType.IMAGE_FILE_BMP : ImageFileType.IMAGE_FILE_INVALID;
                default:
                    return ImageFileType.IMAGE_FILE_INVALID;
            }
        }

        /**
         * Verifies that a byte array starts with a given sequence
         */
        private bool ByteArrayStartsWith(byte[] inputA, params byte[] inputB)
        {
            if (inputA.Length < inputB.Length)
            {
                return false;
            }
            else
            {
                for (int i = 0; i < inputB.Length; i++)
                {
                    if (inputA[i] != inputB[i])
                    {
                        return false;
                    }
                }
            }

            return true;

        }
    }
}